import React, { useState } from "react";

function InputArea() {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

    function handleChange(event) {
        setNote()
    }
  return (
    <form>
          <input onChange={handleChange} name="title"
         placeholder="Title..." />
      <textarea
        onChange={handleChange}
        name="content"
        placeholder="Content..."
      />
      <button>Add</button>
    </form>
  );
}

export default InputArea;

